mapper.select:
select *
from member
where m_id = #{m_id}
and m_pw = #{m_pw}
and M_WITHDRAWAL = 'N'

select * 
from member 
where m_name = #{m_name}
and m_tel = #{m_tel}

select * 
from member 
where m_name = #{m_name}
and m_tel = #{m_tel}
and m_id = #{m_id}

mapper.update:
				update member set 	  m_name = #{m_name}
					                , m_tel = #{m_tel}
					                , m_area = #{m_area}
					                , m_field = #{m_field}
					                , m_img = #{m_img}
                where m_id = #{m_id}

mapper.delete:
delete from reginfo
where m_id = #{m_id}
and bt_num = #{bt_num}
and bc_num = #{bc_num}
and p_num = #{p_num}

mapper.update:
update bookmark set b_reg = 'Y'
where m_id = #{m_id}
and bt_num = #{bt_num}
and bc_num = #{bc_num}
and p_num = #{p_num}

mapper.delete:
delete from bookmark
