mapper.update:
update member set M_WITHDRAWAL = 'Y'
where m_id = #{m_id}
and m_pw = #{m_pw}

mapper.select:
select *
from reginfo NATURAL JOIN member
where bt_num = #{bt_num}
and bc_num = #{bc_num}
and p_num = #{p_num}

